DROP TABLE IF EXISTS [article];
CREATE TABLE [article]([rowid] INTEGER, [name] VARCHAR, [content] VARCHAR);

INSERT INTO [article]([rowid], [name], [content])
VALUES(1, '失落少年', '思索过去的泪痕，
回忆流逝的时光，
那些年，
疯疯打打的我们，
一起挥舞着青春，
笑容纯真的女孩，
歌声洋溢的书生，
迷恋上女孩的吻，
却无法得到认肯，
伤心万分的人，
三年之等，
学习分散过心神，
考试从没有认真，
现在还未有觉醒，
仍在爱情中沉沦，
出卖天真的灵魂，
逐渐的变得心狠，
孤苦的人在背后，
独自心疼。');

INSERT INTO [article]([rowid], [name], [content])
VALUES(2, '对与错', '无味的洒脱，
